import '../index.css';


import InputwithButton from "../components/inputsave.js";
import Icon from "../components/icon.js";


import React, { useEffect, useState, useRef, Component }  from 'react';



let changeQuiz = [{
	    id: 1,
	    question: "Wie geht es dir?",
	    answers: [
	      {   
	        answer: "mir geht es sehr gut",
	        isRight: false
	      },{
	        answer: "mir geht es gut",
	        isRight: false
	      },{
	        answer: "mir geht es schlecht",
	        isRight: false
	      },{
	        answer: "mir geht es sehr schlecht",
	        isRight: false
	      },
	    ]}
	  ];


function NewQuizCreator() {



	let [newQuiz, setNewQuiz] = useState(changeQuiz);

  	function handleInputSave(questionIndex, answerIndex, newText, newBoolean){
	    let oldQuiz = newQuiz;
	    if (answerIndex === -1){
	      oldQuiz[questionIndex].question = newText;
	    } else {
	      oldQuiz[questionIndex].answers[answerIndex].answer = newText;
	      oldQuiz[questionIndex].answers[answerIndex].isRight = newBoolean;

	    }

	    setNewQuiz(oldQuiz);
	    console.log(newQuiz);
  	}

  	function deleteAnswer(questionIndex, answerIndex) {
  		let oldQuiz = newQuiz.slice();
  		oldQuiz[questionIndex].answers.splice(answerIndex, 1);
  		console.log(oldQuiz)
  		setNewQuiz(oldQuiz);
  		console.log(newQuiz);
  	}

  	function addQuestion(questionIndex){
  		console.log(newQuiz);
  		console.log(newQuiz.slice(0, questionIndex));
  		setNewQuiz(newQuiz.slice(0, questionIndex).concat({
			id: newQuiz.length + 1,
			question: "Was ist deine Lieblingsfarbe?",
			answers: [
				{
					answer: "Blau",
					isRight: true
				},
				{
					answer: "Rot",
					isRight: false
				},
				{
					answer: "Gelb",
					isRight: false
				},
				{
					answer: "Pink",
					isRight: false
				}
			]												
		}).concat(newQuiz.slice(questionIndex, newQuiz.length)));
		console.log(newQuiz.slice(0, questionIndex));
		console.log(newQuiz);

  	}

  	function addAnswer(questionIndex){
  		let oldQuiz = newQuiz.slice();
  		console.log(oldQuiz[questionIndex].answers);
  		oldQuiz[questionIndex].answers.push({
  			answer: "Im very good",
  			isRight: true
  		});
  		console.log(oldQuiz[questionIndex].answers);
  		setNewQuiz(oldQuiz);

  	}

	return (
		<div className="flex justify-center">

			<div className="flex flex-col w-auto justify-center">
				{
					newQuiz.map(

						(Question, questionIndex) => (
								<>
									<div 
										className="w-6 cursor-pointer justify-center flex  m-auto mb-5 mt-5"
										onClick={
											() => addQuestion(questionIndex)


										}>
										<img 
											src="https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg" 
											className="transform duration-300 hover:scale-150"
											alt="plus"

											/>	
									</div>
									<div key={Question.id}
										className="w-auto border-yellow-200 border-8 p-3 bg-pink-200 shadow3 rounded-lg">
										<InputwithButton border="border-black border-2 p-4"
														 text={Question.question}
														 index={-1}
														 position={questionIndex}
														 save={(questionIndex, answerIndex, newText, newBoolean) => handleInputSave(questionIndex, answerIndex, newText, newBoolean)}
														 boolean = {false} 
														 deleteAnswer={(questionIndex, answerIndex) => deleteAnswer(questionIndex, answerIndex)}
										/>
										{
											Question.answers.map(
												(answer, answerIndex) => (
														<InputwithButton border=""
																		 text={answer.answer}
																		 index={answerIndex}
																		 position={questionIndex}
																		 save={(questionIndex, answerIndex, newText, newBoolean) => handleInputSave(questionIndex, answerIndex, newText, newBoolean)}
																		 boolean = {answer.isRight}
																		 deleteAnswer={(questionIndex, answerIndex) => deleteAnswer(questionIndex, answerIndex)}
														/>
													)
												)
										}
										<Icon source={"https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg"} click={() => addAnswer(questionIndex)} type="middle" param={questionIndex}/>
									</div>
																	</>
							)
						)
				}
				<div 
										className="w-6 cursor-pointer justify-center flex  m-auto mb-5 mt-5"
										onClick={
											() => {


													setNewQuiz(newQuiz.concat({
																id: newQuiz.length + 1,
																question: "Was ist deine Lieblingsfarbe?",
																answers: [
																{
																	answer: "Blau",
																	isRight: true
																},
																{
																	answer: "Rot",
																	isRight: false
																},
																{

																	answer: "Gelb",
																	isRight: false
																},
																{
																	answer: "Pink",
																	isRight: false
																}


																]												
																}
															));
													console.log(newQuiz);

												}	
										}>
										<img 
											src="https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg" 
											className="transform duration-300 hover:scale-150"
											alt="plus"

											/>	
									</div>
			</div>
		</div>


		)
}

export default NewQuizCreator;
