import '../index.css';

import Haekchen from "./haekchen.js";




import React, { useEffect, useState,  Component }  from 'react';

const InputwithButton = ({border, text, index, position, save}) => {

	useEffect(() => {
		setInput(text)
	}, [text])


	let [input, setInput] = useState(text);
	let [isRight, setIsRight] = useState(false);

	function clickParent(){
		console.log(isRight);
		if (isRight == false) {
			setIsRight(true)
		}	else {
			setIsRight(false)
		}
		console.log(isRight);
	}

	return	(

		<div className="flex justify-center">

			<input 
				className={"rounded-xl hover:scale-150 p-1 m-1 " + border}
				value={input}
				onChange={ e => setInput(e.target.value)}/>
			<div 
				className="w-6 cursor-pointer align-middle  m-3"
				onClick={() => save(position, index, input)}>
				<img 
					src="https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg" 
					className="transform duration-300 hover:scale-150"
					alt="plus"

					/>	
			</div>
			<Haekchen clickParent={clickParent}/>
		</div>
		)
}


export default InputwithButton;
