import './index.css';



function MiniQuestion(props) {





  return (
    <div className="text-sm p-1 w-20 rounded-xl border-4 border-blue-200">
      <div className="rounded-xl border-2 border-pink-200">
        {props.question}
      </div>
      {
        props.answers.map(
          object => (
            <div className={"border-2 rounded-xl" + (object.isRight ? "border-green-200" : "border-red-200")}>
              {object.answer}
            </div>
          )
        )
      }



    </div>
  )
}


export default MiniQuestion;
