import '../index.css';


import InputwithButton from "../components/inputsave.js";

import React, { useEffect, useState, useRef, Component }  from 'react';


function NewQuizCreator() {



 	let [newQuiz, setNewQuiz] = useState([
	  	{
	    id: 1,
	    question: "This is adsfsdf example question",
	    answers: [
	      {   
	        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
	        isRight: false
	      },{
	        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
	        isRight: true
	      },{
	        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
	        isRight: false
	      },{
	        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
	        isRight: false
	      },
	    ]},
	  {
	    id: 2,
	    question: "This is sfsdfa example question",
	    answers: [
	      {
	        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
	        isRight: false
	      },{
	        answer: "answer nr.3dsfsdfsdfsafadfsdfsdfsdfsdf",
	        isRight: true
	      },{
	        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
	        isRight: false
	      },{
	        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
	        isRight: false
	      },
	    ]},
	  {
	    id: 3,
	    question: "Tsdfsdhis is a example question",
	    answers: [
	      {
	        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
	        isRight: false
	      },{
	        answer: "answer nr.1dsfsdfsdfsafadfsdfsdfsdfsdf",
	        isRight: true
	      },{
	        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
	        isRight: false
	      },{
	        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
	        isRight: false
	      },
	    ]},
	  {
	    id: 4,
	    question: "Thsdfsfis is a example question",
	    answers: [
	      {
	        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
	        isRight: false
	      },{
	        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
	        isRight: true
	      },{
	        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
	        isRight: false
	      },{
	        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
	        isRight: false
	      },
	    ]}]);

  	function handleInputSave(questionIndex, answerIndex, newText){
	    let oldQuiz = newQuiz;
	    if (answerIndex === -1){
	      oldQuiz[questionIndex].question = newText;
	    } else {
	      oldQuiz[questionIndex].answers[answerIndex] = newText;
	    }

	    setNewQuiz(oldQuiz);

  	}



	return (
		<div className="flex justify-center">
			<div className="flex flex-col w-auto justify-center">
				{
					newQuiz.map(
						(object, index) => (
								<>
									<div 
										className="w-6 cursor-pointer justify-center flex  m-3"
										onClick={() => console.log("Hello")}>
										<img 
											src="https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg" 
											className="transform duration-300 hover:scale-150"
											alt="plus"

											/>	
									</div>
									<div className="w-auto border-yellow-200 border-8 bg-pink-200 shadow3 rounded-lg">
										<InputwithButton border=""
														 text={object.question}
														 index={-1}
														 position={index}
														 save={(questionIndex, answerIndex, newText) => handleInputSave(questionIndex, answerIndex, newText)}
										/>
									</div>
								</>
							)
						)
				}
			</div>
		</div>


		)
}

export default NewQuizCreator;
