import '../index.css';

import React, { useEffect, useState,  Component }  from 'react';


function MiniQuestion(props, ref) {



  function isSelected(){
    console.log("I am the " + ref + ". Question")
  }

  return (
    <div 
      className="text-sm p-3 w-32 transform ease-in-out duration-1000 shadow3  translateX rounded-xl border-4 border-blue-200"

    >
      <div className="rounded-xl truncate border-2 border-pink-200 p-1 ">
        {props.question.question}
      </div>
      <div>
      {
        props.question.answers.map(
          object => (
            <div className={"border-2 truncate rounded-xl border-blue-200 p-1"}>
              {object.answer}
            </div>
          )
        )
      }
      </div>



    </div>
  )
}


export default MiniQuestion;
