import '../index.css';

import React, { useEffect, useState,  Component }  from 'react';

const InputwithButton = (props) => {
	let { array } = props;
	let [ border, text, answerIndex, questionIndex, handleSave ] = array;
	let [input, setInput] = useState(text);


	return	(
		<div className="flex justify-center">
			<input 
				className={"rounded-xl p-1 m-1 " + border}
				value={input}
				onChange={ e => setInput(e.target.value)}/>
			<div 
				className="w-6 cursor-pointer align-middle  m-3"
				onClick={() => handleSave(questionIndex, answerIndex, input)}>
				<img 
					src="https://assets.dryicons.com/uploads/icon/svg/12630/0be0f588-bd44-4436-bf6e-54498fbf74dd.svg" 
					className="transform hover:scale-150"
					alt="plus"

					/>	
			</div>
		</div>
		)
}


export default InputwithButton;
