import '../index.css';

import MiniQuestion from "../components/miniquestion.js";



import React, { useEffect, useState, useRef, Component }  from 'react';



function QuizCreator() {
  let position = 0;


  let newQuiz = [
  {
    question: "This is adsfsdf example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "This is sfsdfa example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "Tsdfsdhis is a example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "Thsdfsfis is a example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]}
  ];


  let [arrayPosition, setArrayPosition] = useState(0);

  let rs = document.querySelector(":root").style

  useEffect(() => {
    console.log(newQuiz.length);
    console.log(arrayPosition)
  }, [arrayPosition])


  function move(direction) {
    position += direction;

    rs.setProperty("--xposition", position + "px")
    console.log(arrayPosition);
  }



  return (
    <div>
      <div className="flex justify-center p-3">
        <div className="w-32 flex ">
        {
          newQuiz.map((Question, index) => (

            <MiniQuestion key={Question.answer}question={Question}  />

          ))
        }

        </div>
      </div>
      <div className="flex justify-center">
        <div
          className=" w-12 cursor-pointer m-3 text-center transform ease-in-out duration-500 hover:scale-150 active:scale-110 p-1 shadow3 rounded-xl "
          onClick={() => move(-128)}
        >
          <img  className="transform rotate-180" src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
        <div
          className=" w-12 cursor-pointer m-3 text-center  transform ease-in-out duration-500 hover:scale-150 p-1 shadow3 rounded-xl "
          onClick={() => move(128)}
        >
          <img   src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
      </div>


      <div className="flex  justify-center">
        <div className="text-center bg-yellow-200 rounded-xl border-2 border-black">
                         <div 
                 className="text-sm p-3 w-32 transform ease-in-out duration-1000 shadow3  translateX rounded-xl border-4 border-blue-200"

              >
              <div className="rounded-xl truncate border-2 border-red-700 p-1 ">
                {newQuiz[arrayPosition].question.question}
                {arrayPosition}
              </div>
              <div>
               {
                 newQuiz[arrayPosition].answers.map(
                   (object, index) => (
                   <input type="text" value={newQuiz[arrayPosition].answers[index]} onChange={e => object = e.target.value}/> 
                )
                )
               }
              </div>



          </div>



      </div>
      </div>
      <div className="flex justify-center">
        <div
          className=" w-12 cursor-pointer m-3 text-center transform ease-in-out duration-500 hover:scale-150 active:scale-110 p-1 shadow3 rounded-xl "
          onClick={() => setArrayPosition(arrayPosition - (arrayPosition > 0) ? 1: 0)}
        >
          <img  className="transform rotate-180" src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
        <div
          className=" w-12 cursor-pointer m-3 text-center  transform ease-in-out duration-500 hover:scale-150 p-1 shadow3 rounded-xl "
          onClick={() => setArrayPosition(arrayPosition + (arrayPosition <= newQuiz.length) ? 1: 0)}
        >
          <img   src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
      </div>

      <div>
      </div>
    </div>
  )
}


export default QuizCreator;
