import '../index.css';



function MiniQuestion(props) {

  (props.position <= 0
    ?  "translate-x-" + props.position
    : "-translate-x-" + props.position)



  return (
    <div className={"text-sm p-3 w-32 transform ease-in-out duration-75 hover:rotate-90 " + props.position + " rounded-xl border-4 border-blue-200"}>
      <div className="rounded-xl truncate border-2 border-pink-200 p-1 ">
        {props.question.question}
      </div>
      <div>
      {
        props.question.answers.map(
          object => (
            <div className={"border-2 truncate rounded-xl border-blue-200 p-1"}>
              {object.answer}
            </div>
          )
        )
      }
      </div>



    </div>
  )
}


export default MiniQuestion;
