import '../index.css';

import React, { useEffect, useState, useRef, Component }  from 'react';


function SlideInDialogBox({func, isOn}){





    const styles = { 
        transform: `translate(-50vw, -50vh)` 
    };
    let [name, setName] = useState("");
    let [background, setBackground] = useState("");


    function move(x, y) {
    	return `translate(${x}vw, ${y}vh)`
    }



    useEffect(() => {
		if (isOn == true) {
			styles.transform = move(50, 50);
			setBackground("bg-black")
		} else {
			setBackground("");
			styles.transform = move(-50, -50)
		}
	}, [])




    return (
    	<div className={"width-auto height-auto z-10 opacity-25 transform duration-300 ease-in-out " + background }>
			<div  className="p-1 z-20 mx-auto  flex justify-center">
				<p> Name: </p>
        		<input 
        			className="rounded-xl tranform duration-300 ease-in-out hover:scale-110 p-1 m-1 border-black border-2 p-4"
        			value={name}
        			onClick={e => setName(e.target.value)}

        		/>
      		</div>		

		</div>

    );

}



export default SlideInDialogBox;
