import '../index.css';
import Button from "./button.js";
import React, { useEffect, useState, useRef, Component }  from 'react';


function SlideInDialogBox({func, isOn, getBack}){




    let [name, setName] = useState("");
    let [background, setBackground] = useState("bg-red-700");
    let [pos, setPos] = useState("translate1");



    useEffect(() => {
		if (isOn == true) {

			setBackground("bg-red-700");
			setPos("translate2")
		} else {
			setBackground("bg-red-700");

			setPos("translate1")
		}
	}, [])



    return (
    	<div className={" w-screen h-screen  sticky top-0 z-10 flex justify-center flex-col transform duration-500 ease-in-out bg-pink-200 " + isOn	}>
			<div  className={"m-auto" }>
				<div className="flex justify-center flex-col">
				<p className=""> Name: </p>
        		<input 
        			className="rounded-xl shadow3  transform duration-300 ease-in-out hover:scale-110 p-4   border-black border-2 "
        			value={name}
        			onChange={e => setName(e.target.value)}

        		/>
        		</div>
        		<div className="flex justify-center">
		      		<Button click={() => getBack()}
		      				text="Cancel"
		      		/>
					<Button click={() => {func()}} 
		         			text="Send"
		         	/>
	         	</div>
         	</div>
		</div>

    );

}



export default SlideInDialogBox;
