import logo from './logo.svg';
import './index.css';
import Question from "./components/question.js";



import React, { useEffect, useState,  Component }  from 'react';


function App() {
  let [questionIndex, setIndex] = useState(1);


  let quiz = {
      numberOfQuestions: 2,
      questions: [{
          text: "How many Ducks?",
          answers: ["4", "6", "5", "3"],
          rightAnswer: 0
        },
        {
          text: "How many Dogs?",
          answers: ["5", "3", "55", "6"],
          rightAnswer: 2
        }
      ]
    }

  const handleIndex = () => {
    setIndex(questionIndex + 1);
  }


  return (
    <div className="App">
        <Question question={quiz.questions[questionIndex]} numberOfIndex={questionIndex} numberOfQuestions={quiz.numberOfQuestions}/>
    </div>
  );
}

export default App;
