import MiniQuestion from "../components/miniquestion.js";



import React, { useEffect, useState,  Component }  from 'react';



function QuizCreator() {
  let position = 0;

  console.log()

  let newQuiz = [
  {
    question: "This is adsfsdf example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "This is sfsdfa example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "Tsdfsdhis is a example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]},
  {
    question: "Thsdfsfis is a example question",
    answers: [
      {
        answer: "answer nr.1sadsdadasdadasdadasdfdfgsdfsadfsdfsdfsdfsdf",
        isRight: false
      },{
        answer: "answer nr.2dsfsdfsdfsafadfsdfsdfsdfsdf",
        isRight: true
      },{
        answer: "answer nr.fsdfsdfsdfsdfsdfsdfsdfsdf3",
        isRight: false
      },{
        answer: "answerfsdfsdfsdfsdfsdfsdfsdfdsffsdfdsfsdf nr.4",
        isRight: false
      },
    ]}
  ];




  let [arrayPosition, setArrayPosition] = useState(0);
  let rs = document.querySelector(":root");

  function handlePosition(direction) {
      setArrayPosition(arrayPosition + (direction < 0 ? 1 : -1));
  }

  function move(direction) {
        position += direction;

    console.log(position);
    rs.style.setProperty("--xposition", position + "px");

  }

  function test() {
    console.log("Yooo");
  }
  return (
    <div>
      <div className="flex justify-center p-3">
        <div className="w-32 flex ">
        {
          newQuiz.map((Question, index) => (

            <MiniQuestion question={Question}  position={index} />

          ))
        }

        </div>
      </div>
      <div className="flex  justify-center">
        <div className="text-center bg-yellow-200 rounded-xl border-2 border-black">
           <div className="border-2 m-3 border-red-700 rounded-xl text-sm text-center">
             {arrayPosition}

           </div>

        </div>
      </div>
      <div className="flex justify-center">
        <div
          className=" w-12 cursor-pointer m-3 text-center transform ease-in-out duration-500 hover:scale-110 active:scale-110 p-1 shadow3 rounded-xl "
          onClick={() => {move(-128) ;
      }}
        >
          <img className="transform rotate-180" src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
        <div
          className=" w-12 cursor-pointer m-3 text-center  transform ease-in-out duration-500 hover:scale-110 p-1 shadow3 rounded-xl "
          onClick={() => { move(128)
      }}
        >
          <img  src="https://img.icons8.com/fluency/48/000000/arrow.png"/>
        </div>
      </div>
      <div>
      </div>
    </div>
  )
}


export default QuizCreator;
