import logo from './logo.svg';
import './index.css';
import Question from "./components/question.js";
import AnswerRow from "./components/answerRow.js";

import React, { useEffect, useState,  Component }  from 'react';



function App() {
  let [questionIndex, setIndex] = useState(0);


  let quiz = [{
        text: "How many Ducks?",
        answers: [{
          text: "5",
          isRight: false
        }, {
          text: "2",
          isRight: false
        }, {
          text: "3",
          isRight: true
        }, {
          text: "4",
          isRight: false
        }],

      },{
        text: "How many Dogs?",
        answers: [{
          text: "5123",
          isRight: false
        }, {
          text: "2323",
          isRight: true
        }, {
          text: "3123",
          isRight: false
        }, {
          text: "4123",
          isRight: false
        }],
      }];


  function handleSetIndex() {

    setIndex(questionIndex + 1)
    console.log(questionIndex);
  }



  return (
    <div className="App">

        <Question question={quiz[questionIndex  ]} index={questionIndex} numberOfQuestions={quiz.length}/>
        <AnswerRow answers={quiz[questionIndex ].answers.slice(0, quiz[questionIndex -1 ].answers.length / 2)}
          getFurther={handleSetIndex}/>
        <AnswerRow answers={quiz[questionIndex ].answers.slice(quiz[questionIndex].answers.length / 2, quiz[questionIndex].answers.length) }
           getFurther={handleSetIndex} />
    </div>
  );
}

export default App;
