import logo from './logo.svg';
import './index.css';
import Question from "./components/question.js";



import React, { useEffect, useState,  Component }  from 'react';


function App() {
  let [questionIndex, setIndex] = useState(0);


  let quiz = [{
        text: "How many Ducks?",
        answers: ["4", "6", "5", "3"],
        rightAnswer: 0
      },
      {
        text: "How many Dogs?",
        answers: ["5", "3", "55", "6"],
        rightAnswer: 2
      },];




  return (
    <div className="App">
        <Question question={quiz[questionIndex]}/>
    </div>
  );
}

export default App;
